import { useState } from 'react';
import './App.css';
import TodoInput from './Components/TodoInput';

function App() {
  const [todo,setTodo] = useState([]);

  const handleChange = (e) => {
     setTodo([...todo,e.target.value])
  }
  return (
    <div className="App">
      <TodoInput value={todo} onChange={handleChange}/>
    </div>
  );
}

export default App;
